contract TaoStakingDistributor {
    using SafeMath for uint;
    using SafeERC20 for IERC20;

    address public owner;
    address public vault;
    address public TAO;
    address public BUSD;
    address public DAO;
    address public stakingContract;
   

    uint public nextEpochBlock;
    uint public blocksInEpoch;

    // reward rate is in hundreths i.e. 50 = 0.5%
    uint public rewardRate;

    bool public isInitialized;

    constructor() {
        owner = msg.sender;
    }

    function initialize( uint _nextEpochBlock, uint _blocksInEpoch, uint _rewardRate, address _vault, address _stakingContract, address _TAO, address _BUSD, address _DAO ) external returns ( bool ) {
        require( msg.sender == owner );
        require( isInitialized == false );

        nextEpochBlock = _nextEpochBlock;
        blocksInEpoch = _blocksInEpoch;
        rewardRate = _rewardRate;
        vault = _vault;
        stakingContract = _stakingContract;
        TAO = _TAO;
        BUSD = _BUSD;
        DAO = _DAO;
      
        isInitialized = true;

        return true;
    }

    function distribute() external returns ( bool ) {
        if ( block.number >= nextEpochBlock ) {
            nextEpochBlock = nextEpochBlock.add( blocksInEpoch );

            uint _taoToDistribute = IERC20( TAO ).totalSupply().mul( rewardRate ).div( 10000 );

            IERC20( TAO ).safeTransfer( stakingContract, _taoToDistribute );
            IStaking( stakingContract ).stakeTAO( 0 );
        }
        return true;
    }

    function convertBUSDToTAO( uint _amountToConvert ) external returns ( bool ) {
        require( msg.sender == owner );

        IERC20( BUSD ).approve( vault, _amountToConvert );
        IVault( vault ).depositReserves( _amountToConvert );

        return true;
    }

    function setBlocksInEpoch( uint _blocksInEpoch ) external returns ( bool ) {
        require( msg.sender == owner);
        blocksInEpoch = _blocksInEpoch;
        return true;
    }

    // reward rate is in hundreths i.e. 50 = 0.5%
    function setRewardRate( uint _rewardRate ) external returns ( bool ) {
        require( msg.sender == owner );
        rewardRate = _rewardRate;
        return true;
    }

    function setVault( address _vault ) external returns ( bool ) {
        require( msg.sender == owner );
        vault = _vault;
        return true;
    }

    function setStaking( address _staking ) external returns ( bool ) {
        require( msg.sender == owner );
        stakingContract = _staking;
        return true;

    }

    function transferOwnership( address _owner ) external returns ( bool ) {
        require( msg.sender == owner );
        owner = _owner;

        return true;
    }

    function transferBUSDToDAO() external returns ( bool ) {
        require( msg.sender == owner );
        IERC20( BUSD ).safeTransfer( DAO, IERC20( BUSD ).balanceOf( address( this ) ) );
        return true;
    }

    function transferTAOToDAO() external returns ( bool ) {
        require( msg.sender == owner );
        IERC20( TAO ).safeTransfer( DAO, IERC20( TAO ).balanceOf( address( this ) ) );
        return true;
    }

    function getCurrentRewardForNextEpoch() external view returns ( uint ) {
        return IERC20( TAO ).totalSupply().mul( rewardRate ).div( 10000 );
    }
}
